// helper wrapper
func toBase64Str(data []byte) string {
	return base64.StdEncoding.EncodeToString(data)
}

func fromBase64Str(data string) ([]byte, error) {
	return base64.StdEncoding.DecodeString(data)
}

func TestEllipticArith(t *testing.T) {
	for i, sampleInput := range sampleInputs {
		inputs := strings.Split(sampleInput, ",")
		results := strings.Split(sampleResults[i], ",")

		aValue, err := fromBase64Str(inputs[0])
		assert.NoError(t, err)
		bValue, err := fromBase64Str(inputs[1])
		assert.NoError(t, err)
		nValue, err := fromBase64Str(inputs[2])
		assert.NoError(t, err)

		a := newCurP().setBytes(aValue)
		b := newCurP().setBytes(bValue)
		n := new(big.Int).SetBytes(nValue)

		sum := newCurP().add(a, b)
		assert.True(t, strings.Compare(toBase64Str(sum.bytes()), results[0]) == 0)

		sum.add(sum, a)
		assert.True(t, strings.Compare(toBase64Str(sum.bytes()), results[1]) == 0)

		dbl := newCurP().double(a)
		assert.True(t, strings.Compare(toBase64Str(dbl.bytes()), results[2]) == 0)

		prd := newCurP().mul(a, b)
		assert.True(t, strings.Compare(toBase64Str(prd.bytes()), results[3]) == 0)

		sqr1 := newCurP().sqr(a)
		assert.True(t, strings.Compare(toBase64Str(sqr1.bytes()), results[4]) == 0)

		sqr2 := newCurP().sqr(b)
		sqr2.sqr(sqr2)
		assert.True(t, strings.Compare(toBase64Str(sqr2.bytes()), results[5]) == 0)

		pow := newCurP().powN(b, n)
		assert.True(t, strings.Compare(toBase64Str(pow.bytes()), results[6]) == 0)
	}
}

func TestEllipticPowN(t *testing.T) {
	for i := 0; i < ellipticTestRound; i++ {
		a, err := randGalE(gFR)
		assert.NoError(t, err)

		b, err := randGalE(gFR)
		assert.NoError(t, err)

		gPowA := newCurP().powN(gen, a.val)
		gPowB := newCurP().powN(gen, b.val)

		assert.True(t, gPowA.powN(gPowA, b.val).equal(gPowB.powN(gPowB, a.val)))
	}
}
